function init(input) {
    const INPUT = input.split(" ");
    const KEYPAD = ["123","456","789"];
    const BATHROOM_CODE = [];
    const POSITION = {
        x: 1,
        y: 1,

        isValid() {
            return this.x>=0 && this.x<3 && this.y >=0 && this.y<3;
        },

        move(direction) {
            switch(direction) {
                case "up":
                    this.moveUp();
                    break;
                case "right":
                    this.moveRight();
                    break;
                case "down":
                    this.moveDown();
                    break;
                case "left":
                    this.moveLeft();
                    break;
            }
        },

        moveUp() {
            this.y--;
            this.y += !this.isValid() ? 1 : 0;
        },

        moveRight() {
            this.x++;
            this.x -= !this.isValid() ? 1 : 0;
        },

        moveDown() {
            this.y++;
            this.y -= !this.isValid() ? 1 : 0;
        },

        moveLeft() {
            this.x--;
            this.x += !this.isValid() ? 1 : 0;
        },

        getNext(instruction) {
            const DIRECTION_PARSER = {U: "up", R: "right", D: "down", L: "left"};
            const INSTRUCTION = instruction.split("");
            INSTRUCTION.forEach(direction => this.move(DIRECTION_PARSER[direction]));
        }
    }

    INPUT.forEach(instruction =>{
        POSITION.getNext(instruction);
        BATHROOM_CODE.push(KEYPAD[POSITION.y][POSITION.x]);
    });

    return BATHROOM_CODE.join("");
}

function run() {
    const TEST_INPUT = "ULL RRDDD LURDL UUUUD";
    const INPUT = "    ";
    console.log(init(INPUT));
}

run();